package day12;

public class Switch_Staitment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 3; // if value won't match it will go (default)

		switch(score) {
		case 1:
			System.out.println("Score is 1");
			break; // exit switch staitment
		case 2:
			System.out.println("Score is 2");
			break;
		case 3:
			System.out.println("Score is 3");
			break;
			default: // if variable does not match the value
				System.out.println("invalid score:");

		}













	}

}
