package day17LOOPS;

public class While_Loop_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 0;
		while (num < 5) {
			System.out.println("Murad is the BEST!!!!");
			num++; // add 1 to the num every time untill the condition will become false
		}

	}

}
