package day6;

public class incrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int empoyyes = 50;
	
		System.out.println("Employyes :" + empoyyes);
		
		empoyyes++;
		System.out.println("Empoyyes: " + empoyyes);
		++empoyyes;
		System.out.println("Empoyyes: " + empoyyes);
		empoyyes +=1;
		System.out.println("Empoyyes: " + empoyyes);
		empoyyes = empoyyes + 1;
		System.out.println("Empoyyes: " + empoyyes);
		empoyyes -=6;
		System.out.println("Empoyyes: " + empoyyes);
		empoyyes--;
		System.out.println("Empoyyes: " + empoyyes);
		--empoyyes;
		System.out.println("Empoyyes: " + empoyyes);


		
			
		String str = ("Hello " + "World " + 123);
		System.out.println(str);
		
		
		
	}
	

}
